import React from 'react';
import axios from 'axios'
import Input from '@material-ui/core/Input';

import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';

import './App.css'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      todo_list: [],
      name: "",
    }
    this.createTodo = this.createTodo.bind(this)
    this.getTodo = this.fetchTodo.bind(this)
    this.deleteTodo = this.deleteTodo.bind(this)
  }

  componentDidMount(){
    axios.get('http://localhost/api/Todo/read.php')
    .then(({data: todo}) => this.fetchTodo(todo))
    .catch(error => console.log(error))
  }
  fetchTodo(t){
    this.setState({ todo_list: t })
  }
  createTodo(data) {
    axios.post('http://localhost/api/Todo/create.php', data)
    .then(response=>console.log(response))
    .catch(error => console.log(error))

    // update state to force re-render
    data['id'] = this.state.todo_list.length + 1
    // for deep copy data
    let obj = JSON.parse(JSON.stringify(data))
    this.setState({
        todo_list: this.state.todo_list.concat(obj)
    })
  }
  deleteTodo(id){
    axios.post('http://localhost/api/Todo/delete.php', id)
    .then(response=>console.log(response))
    .catch(error => console.log(error))

    this.setState({
      todo_list: this.state.todo_list.filter(todo => todo.id !== id)
    })
  }

  render() {
    let todo_list = this.state.todo_list
    console.log(this.state.todo_list)

    return (
      <div>
        PHP hello
        <p className="test">123</p>
        <form className="container" noValidate autoComplete="off">
          <Input placeholder="test" required={true}/>
          <TextField
            id="standard-name"
            label="Name"
            className="input-name"
            value={this.state.name}
            onChange={this.handleChange('name')}
            margin="normal"
          />

           <TextField
            id="standard-password-input"
            label="Password"
            className="aa"
            type="password"
            autoComplete="current-password"
            margin="normal"
          />
        </form>

        <br/>
        {
          todo_list &&  
          todo_list.map(
            (todo) => 
              <div key={todo.id}>
                <div> {todo.id}: {todo.name} </div>
                <button onClick={ () => this.handleAddTodo(tmp_data) }>update one</button>
                <button onClick={ () => this.deleteTodo(todo.id) }>delete one</button>
              </div>
          )
        }
        <button onClick={ () => this.createTodo(tmp_data) }>add one</button>
      </div>
    )
  }
}

const tmp_data = {
  "name": "Yee",
  "description" : "Yee",
  "due": "2019-05-15 18:36:41",
  "createdAt": "2019-05-15 18:36:41",
  "updatedAt": "2019-05-15 18:36:41"
}

export default App
